REPLACE INTO `?:newsletter_descriptions` (`newsletter_id`, `newsletter`, `body_html`, `body_txt`, `lang_code`) VALUES ('2','Пример','<h2>Привет,</h2>\r\n\r\n[Ваш текст]\r\n\r\nБольшое спасибо!\r\nВы можете отписаться от рассылки, используя эту ссылку:\r\n%UNSUBSCRIBE_LINK\r\n\r\n%COMPANY_NAME.','Hello,\r\n\r\n[your text]\r\n\r\nThank you!\r\nYou can unsubscribe from the newsletter by using this link:\r\n%UNSUBSCRIBE_LINK\r\n\r\n%COMPANY_NAME.','RU');