INSERT INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('EN', 'text_exim_import_options_note', '<b>Product options import format</b>\n\n\n\n\n\n\n\n<code>\n\n\n\n{$ldelim}%Option ID[_L]%{$rdelim}%Option name%: %Option type%[{$ldelim}%Variant 1 ID%{$rdelim}%Variant 1%,{$ldelim}%Variant 2 ID%{$rdelim}%Variant 2%,{$ldelim}%Variant N ID%{$rdelim}%Variant N%]\n\n\n\n</code>\n\n\n\n\n\n\n\nwhere,\n\n\n\n<p>\n\n\n\n%Option ID% - the id of the option,<br />\n\n\n\n&nbsp;&nbsp;<b>L</b> - optional parameter, means that it is a linked global option of the product.<br />\n\n\n\n%Option name% - the name of the option,<br />\n\n\n\n%Option type% - the option type, can be the following:<br />\n\n\n\n&nbsp;&nbsp;<b>S</b> - selectbox,<br />\n\n\n\n&nbsp;&nbsp;<b>R</b> - radiogroup,<br />\n\n\n\n&nbsp;&nbsp;<b>C</b> - checkbox,<br />\n\n\n\n&nbsp;&nbsp;<b>I</b> - simple input,<br />\n\n\n\n&nbsp;&nbsp;<b>T</b> - textarea.<br />\n\n\n\n</p>\n\n\n\n<p>\n\n\n\n%Variant N ID% - the id of the variant,<br />\n\n\n\n%Variant N% - the name of the variant. Variants must be specified if the option type is a selectbox or radiogroup only.\n\n\n\n</p>\n\n\n\n\n\n\n\n<p>\n\n\n\nSeveral options must be delimited by semicolon.\n\n\n\n</p>\n\n\n\n\n\n\n\n<b>Examples:</b><br />\n\n\n\n<p>\n\n\n\nSimple text options:\n\n\n\n<code>\n\n\n\nYour age: I; Date of birth: I; Notes: T\n\n\n\n</code>\n\n\n\n</p>\n\n\n\n\n\n\n\n<p>\n\n\n\nOptions with variants:\n\n\n\n<code>\n\n\n\nColor: S[Red, Green, Blue]; Size: R[X, XL, XXL]\n\n\n\n</code>\n\n\n\n</p>');
