INSERT INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('EN', 'text_exim_import_features_note', '<b>Product features import format</b>\n<code>\n{$ldelim}%Feature ID%{$rdelim} (%Group name%) %Feature name%: %Feature type%[%Feature value%]\n</code>\n\nwhere,\n<p>\n%Feature ID% - the id of feature,<br />\n%Group name% - the name of feature group,<br />\n%Feature name% - the name of feature,<br />\n%Feature type% - the feature type (C - checkbox, M - multiple checkboxes, S - text select box, N - number select box, E - extended selectbox, T - simple text, O - number, D - date),<br />\n%Feature value% - the feature value (several values can be delimited by comma)\n</p>\n\n<p>\nSeveral features must be delimited by semicolon.\n</p>\n\n<b>Example:</b><br />\n<code>\nISBN: T[1233423423]; Release date: D[05/05/07]; Color: S[Red]        \n</code>');
