INSERT INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('EN', 'text_commercial_promotion', 'This add-on is available only in the commercial versions of CS-Cart. Please buy either a <a href=\"http://www.cs-cart.com/order-now.html\" target=\"_blank\">CS-Cart Professional</a> or <a href=\"http://www.cs-cart.com/buy-multivendor.html\" target=\"_blank\">CS-Cart Multi-Vendor</a> license for your domain to proceed. After your payment log in to your <a href=\"http://www.cs-cart.com/helpdesk\" target=\"_blank\">Customer Help Desk account</a> and open the <a href=\"http://www.cs-cart.com/index.php?dispatch=licenses.manage\" target=\"_blank\">Licenses</a> page to see your current and new license numbers. Enter your current license number (if it is not) on the <a href=\"[admin_index]?dispatch=settings.manage&section_id=Upgrade_center\" target=\"_blank\">Upgrade center settings page</a> of your store, then click on the <a href=\"[admin_index]?dispatch=upgrade_center.manage_editions\" target=\"_blank\">Update edition</a> link on the Upgrade center page, enter your new license number into the appropriate input field and click the Update button to start the conversion process.');
