INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('11', 'EN', 'Collegiate 72 Tee', '', '', '<p>It\'s not how old you are, it\'s how stylish, and this men\'s adidas  Originals Collegiate 72 Tee shirt feels just as fresh as it was the day  the Trefoil was born in 1972. That\'s why this t-shirt features a  collegiate \"72\" on the front.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('12', 'EN', '100g Pants', '', '', '<p>When coach calls you off the bench, you need warm-up pants that come off  in three seconds or less. That&rsquo;s why these men\'s adidas 100g basketball  pants have tear-away snaps down the sides, so you\'re ready for action  as fast as a superhero.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('13', 'EN', 'Your Idea, Inc. 12 Steps to Building a Million Dollar Business -- Starting Today!', '', '', '<p>&nbsp;</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('14', 'EN', 'Toshiba 40E220U 40\" Class 1080P HD LCD TV', '', '', '<p>Enjoy a stunning picture bursting with color and clarity with the 40-inch Toshiba 40E200U LCD HDTV (40-inch diagonal screen size), featuring a 1080p Full HD resolution that provides twice the pixel resolution of 720p HD models. It provides a number of convenient home entertainment options and key picture quality features, including DynaLight dynamic backlight control for deeper black levels and four HDMI digital inputs for simple high quality connection to cable/satellite boxes, DVD players and more. Plus, the HDMI ports feature fast InstaPort switching for quick changes from one source to another.&nbsp;<br /><br />DynaLight automatically adjusts the backlight intensity based on the image content.<br /><br /> With the Gaming Mode function, you\'ll experience reduced game control delay and improved reaction time--perfect for PC gaming--and the Photo Frame capability with Auto Slide Show enables you to display slideshows of your favorite images when not watching TV programming. <br /><br /> Boasting a slimming tapered bezel and attractive front panel gradation, the new Horizon design creates a more modern, elegant appearance that also adds a subtle luminance to the logo to further its stylish appeal. Other features include MP3 playback via the integrated USB port, a PC input, and a removable stand.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('15', 'EN', 'Nike Chambray Super Runner Men\'s Jacket', '', '<p>The  timeless Windrunner design gets a premium upgrade in the Nike Chambray  Super Runner Men\'s Jacket. Created with tough, water-resistant fabric  and a mesh lining, this jacket combines breathability and protection  from the elements.</p>', '<div class=\"text description black-white\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('16', 'EN', '3D Blu-ray Disc Player (BD-D7500)', '', '', '<p>Whether you are viewing a premium-quality 2D picture or seeing incredible 3D depth, images are breathtakingly real in Samsung Full HD. Combine with a 3D TV and 3D active shutter glasses to view the latest Hollywood 3D titles or amplify your viewing experience by upconverting 2D content to immersive 3D.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('17', 'EN', '101 Things Everyone Should Know About Economics A Down and Dirty Guide to Everything from Securities and Derivatives to Interest Rates and Hedge FundsAnd What They Mean For You', '', '', '<p><span style=\"color: #000000; font-family: Arial, sans-serif; font-size: 12px; background-color: #ffffff;\">What you don\'t know about economics can hurt you&mdash;now more than ever. This easy-to-understand guide answers all the questions you need to know to secure your financial future, such as:</span></p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('18', 'EN', '3D Blu-ray Disc Player (BD-D7000)', '', '', '<p>Whether you are viewing a premium-quality 2D picture or seeing incredible 3D depth, images are breathtakingly real in Samsung Full HD. Combine with a 3D TV and 3D active shutter glasses to view the latest Hollywood 3D titles or amplify your viewing experience by upconverting 2D content to immersive 3D.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('19', 'EN', 'Toshiba BDX2150 Blu-ray Player', '', '', '<p>Enjoy the clarity of Blu-ray and the convenience of streaming media with the Toshiba BDX2150 Blu-ray Disc player. Wi-Fi ready for clutter-free in-home connectivity (Wi-Fi adapter required, sold separately), this player lets you enjoy your favorite movies with stunning native 1080p Full HD output plus surround sound with Dolby audio enhancements. Internet connectivity enables you to take advantage of popular streaming services such as Blockbuster, Netflix*, CinemaNow, Pandora, Vudu and YouTube. In addition, BD-Live 2.0 technology allows you to access internet-only bonus material available on many Blu-ray titles. Offering both USB and HDMI with CEC ports, this player is equipped with the latest command and control functionality.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('20', 'EN', 'The ABCs of Gold Investing. How to Protect and Build Your Wealth with Gold', '', '', '<p><em style=\"color: #000000; font-family: Verdana; background-color: #ffffff; font-size: small;\"><strong>The ABCs of Gold Investing</strong></em><span style=\"color: #000000; font-family: Verdana; background-color: #ffffff; font-size: small;\">&nbsp;(Second Edition) is a solid resource for investors wishing to understand the benefits of private gold ownership. In this reader-friendly book, author Michael J. Kosares addresses the rapid changes in both the American and world economies and explains why gold is important to you as an investor. And, with thirty years experience in the gold business, he can tell you how to add gold to your portfolio in a safe and economical manner.</span></p>', '', '', '', '', '');
