INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('26', 'EN', 'Mac OS X Lion: The Missing Manual', '', '', '<table style=\"color: #333333; font-family: Arial, Verdana, Geneva, Helvetica, sans-serif; font-size: 12px; line-height: 16px; text-align: left; background-color: #ffffff;\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('27', 'EN', 'Trefoil Logo Hoodie', '', '<p>This classic women\'s adidas Originals Trefoil Logo Hoodie is made of  comfortable fleece with a drawcord hood, a kangaroo pocket and an  outsize logo. The pullover hoodie also features extra-long cuffs and hem  for feminine style.</p>', '<div class=\"productText\" style=\"display: block;\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('28', 'EN', 'Run Perf 7/8 Ti', '', '', '<p>adidas Run Perf 7/8 Ti , new to site, more details coming soon.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('29', 'EN', 'TomTom GO LIVE Top Gear edition', '', '', '<p>The TomTom GO LIVE Top Gear edition satnav! Featuring the voice of Jeremy Clarkson, plus a heap of Top Gear bonus goodies, this special edition satnav is the ultimate traffic- vanquishing weapon for the discerning driverist. With speed camera warnings, information via Local Search with Google&trade;, weather reports and more, it&rsquo;ll spirit you onto gloriously empty roads when all about you are stuck in jams. With the calming presence of Jeremy Clarkson himself by your side, what could possibly go wrong?</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('30', 'EN', 'GO 2535 TM WTE', '', '', '<p>The TomTom GO series is the ultimate in driving intelligence. The new, super-slim TomTom GO series holds the latest navigation technology, in a fresh, sleek design and includes a magnetic, click &amp; lock mount for maximum mounting security. Travel confidently with superior routing and the most accurate, dependable maps in the GPS industry.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('31', 'EN', 'Nike Classic Solid Women\'s Jersey Shorts', '', '', '<div class=\"text dark-grey-heather-white description\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('32', 'EN', 'nvi 3450', '', '', '<p>Garmin Guidance 3.0 allows you to quickly look up addresses and services and be guided to your destination with voice-prompted, turn-by-turn directions that speak street names. It comes preloaded with maps that feature 3-D landmarks and terrain for North America. It also comes preloaded with more than 8 million points of interest and offers the ability to add your own.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('33', 'EN', 'nvi 2455LT', '', '', '<p>Whether you&rsquo;re driving through an unfamiliar city or on your daily commute, n&uuml;vi 2455LT makes your trip easier than ever before. It includes FREE lifetime traffic updates, advanced lane guidance and more.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('34', 'EN', 'Nike \"Untouchable\" Women\'s Tank Top', '', '', '<div class=\"text description dark-grey-heather-dark-grey-heather-red\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('35', 'EN', 'Managing Enterprise Content: A Unified Content Strategy, 2nd Edition', '', '', '<p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('36', 'EN', 'Routing Protocols and Concepts: CCNA Exploration Companion Guide', '', '', '<p>&nbsp;</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('37', 'EN', 'DPX308U', '', '', '<p>Many cars come with a 2DIN opening so swap that basic factory radio out for a DPX308U! This full featured CD-receiver comes with front USB input which provides a simple connection for your iPod/iPhone, allowing you to listen to your favorite music while controlling it easily from you your car stereo. When connecting an iPod or iPhone to the USB port you get the added benefit of advanced search features designed to let you spend more time listening to your favorite songs instead of hunting for them.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('38', 'EN', 'Nike Futura Unravel Women\'s T-Shirt', '', '', '<div class=\"text black-gold description\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('39', 'EN', 'DEH-80PRS', '', '', '<p>The DEH-80PRS CD receiver features audiophile grade internal components and materials that are carefully selected to achieve Pioneer\'s highest standards for the ultimate in sound quality. Pioneer&rsquo;s exclusive sound field technologies including Auto EQ and Auto Time Alignment help optimize audio in ways that perfectly suit particular listening spaces and allow subtle manual control of settings. Versatile connectivity helps deliver exceptional sound quality from various digital devices.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('40', 'EN', 'DEH-P8400BH', '', '', '<p>The DEH-P8400BH CD receiver features a full-dot LCD display with Pioneer\'s new MIXTRAX technology, built-in Bluetooth&reg; for hands-free calling and audio streaming, and an HD Radio&trade; tuner. A variety of multimedia options ensures that you will be properly entertained. Customizable audio functions make it easy to suit your listening taste.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('41', 'EN', 'Gift Set (Onesie Plus Socks)', '', '', '<p>Welcome the new addition with this adidas  Originals Gift Set, featuring a one-piece romper and matching socks. The  romper has a sleepy-time Trefoil character on the back, while the front  bristles with fun in vibrant stripes.</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('42', 'EN', 'DEH-1300MP', '', '', '<p>With music in so many different formats, it only makes sense to have a CD receiver that can play them all. Take your digital songs on the go by copying them directly onto a CD-R or CD-RW disc in either MP3 or WMA formats. Since these files are digitally encoded, you will be able to see detailed track information such as artist, song title, and album.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('43', 'EN', 'FH-P8000BT', '', '', '<p>The FH-P8000BT is smartly designed to expand your system, customize your experience, provide superior sound quality and easy operation.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('44', 'EN', 'U.S. History and Government, Second Edition', '', '', '<p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('45', 'EN', 'The Process of Research in Psychology', '', '', '<p><strong style=\"color: #000000; font-family: Arial, Verdana, \'Sans Serif\'; line-height: 15px; text-align: justify;\">A student friendly, thoughtfully organized introduction to research in psychology&nbsp;</strong><br style=\"color: #000000; font-family: Arial, Verdana, \'Sans Serif\'; line-height: 15px; text-align: justify;\" /><br style=\"color: #000000; font-family: Arial, Verdana, \'Sans Serif\'; line-height: 15px; text-align: justify;\" /><span style=\"color: #000000; font-family: Arial, Verdana, \'Sans Serif\'; line-height: 15px; text-align: justify;\">Employing numerous examples and the pedagogical approach of spaced repetition, this introductory text provides a step-by-step explanation of how to design, conduct, and present a research study in psychology. Chronologically organized and chock-full of pedagogy, this book creates logical scaffolding upon which students can build their knowledge.</span><br style=\"color: #000000; font-family: Arial, Verdana, \'Sans Serif\'; line-height: 15px; text-align: justify;\" /></p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('46', 'EN', 'The Official SAT Study Guide: Second Edition(TM)', '', '', '<p>&nbsp;</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('47', 'EN', 'MARVEL VS. CAPCOM 3: FATE OF TWO WORLDS SPECIAL EDITION (PS3)', '', '', '<p><span style=\"color: #242424; font-family: Arial, Verdana, Helvetica, sans-serif; text-align: left; background-color: #ffffff; font-size: xx-small; padding: 0px; margin: 0px;\">The ultimate \"VS.\" Series was born here!</span></p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('48', 'EN', 'TS-MR2040', '', '', '<p>This is marine audio at its best. This 8\" two-way speaker is built specifically for marine use, with heavy-duty construction that strenuously resists water, corrosion, heat&mdash;and boring music. Pioneer research showed that more bass is required for boating applications, and this 8\" size delivers.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('49', 'EN', 'TS-MR1640', '', '', '<p>Whether you\'re out on the water or at the dock, this 6-1/2&rdquo; two-way speaker adds a new dimension to your boat. It\'s built specifically for marine use, with heavy-duty construction inside and out that strenuously resists water, corrosion, heat, and sunlight.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('50', 'EN', 'TS-D1720C', '', '', '<p>Absolute fidelity to musical sources takes form from speakers that reproduce the ambience in which sounds originate. Stage size, musicians&rsquo; movement, aural reflection and other distinctive details all bring these sounds to life. Pioneer D-Series speakers are available in 2-way component packages or 2-way coaxial designs in multiple sizes that fit most vehicles.</p>', '', '', '', '', '');
