INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('79', 'EN', 'adiZero Tempaia Shoes', '', '', '<p>The adiZero&trade; Tempaia shoes by adidas are lightweight, breathable tennis  shoes designed for multi-court use. They feature a breathable upper,  adiPRENE&reg;+ in the forefoot for propulsion, adiPRENE&reg; heel cushioning and  EXTENDED TORSION&reg; for support.</p>', '', '', '', '', '');
