INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('116', 'EN', 'I Am Legend (DVD) (WS)', '', '', '<p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('119', 'EN', 'The Big Bang Theory: The Complete Fourth Season', '', '', '<p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('120', 'EN', 'Samsung Galaxy S II, Epic 4G Touch (Black)', '', '', '<p>The fastest, state-of-the-art networks are 4G, so naturally, the Epic&trade;  4G Touch is designed for speed. With 4G, every download, upload, app,  game, connection and feature runs almost as fast as you can push the  button.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('121', 'EN', 'Fringe: The Complete Third Season', '', '', '<p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('122', 'EN', 'Evolution SST.2 XO Complete Bike 10SPD12', '', '', '<p><span class=\"content\">The all new ICT bred Evolution 29er boasts 120mm  of fully active travel. Combine the Evolution sst.2 120 frame with a set  of fast rolling 29\" wheels and you have a machine that can take you  further than you ever thought possible.</span></p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('123', 'EN', 'Supernatural: The Complete Sixth Season', '', '', '<p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('124', 'EN', 'Truth SST.2 X9 Complete Bike 10SPD \'12', '', '', '<p><span class=\"content\">The legendary performance of the 100mm travel  Truth sst.2 has been enhanced through fine tuning and tweaking over  countless seasons of XC and endurance racing in every condition  imaginable. With Ellsworth\'s proprietary ICT suspension, SST tubing,a  lighter and stronger pivot integrated seat tube and a semi-integrated  tapered head tube - this is the fastest Truth ever created. </span></p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('125', 'EN', 'Samsung Galaxy S II', '', '', '<dl><dt>The Galaxy S II has an amazing 4.27\" Super  AMOLED Plus screen. This superb display has razor sharp clarity and  breathtaking color. See every detail of videos and pictures. See the  full range of hues in all their subtlety and brilliance. And with its  superior wide viewing angle, you don\'t have to be directly in front of  the screen to see a perfect image. Truth is, you really have to see it  to believe it.</dt></dl>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('126', 'EN', 'Casio PRIZM fx-CG10', '', '', '<p><br /><img style=\"word-wrap: break-word; border-style: initial; border-color: initial;\" src=\"http://www.casio.com/resource/images/pressreleases/fx-CG10_Prizm_press_2.jpg\" alt=\"PRIZM fx-CG10\" align=\"right\" /><strong style=\"word-wrap: break-word;\">High-resolution color LCD with full textbook-style display</strong><br style=\"word-wrap: break-word;\" /><br style=\"word-wrap: break-word;\" />The color LCD offers full textbook-style display with the highest pixel resolution* (82,944 dots) and widest color reproduction* (65,536 colors) of any calculator. PRIZM&trade; comes with high-resolution fonts up to 18x24 dots (including blank dots), to enable equations and text to be displayed just like they appear in textbooks. The Blanview LCD delivers superior visibility and low power consumption, enabling color display with an estimated run time of 140 hours on four AAA-size alkaline batteries.**<strong style=\"word-wrap: break-word;\"><br style=\"word-wrap: break-word;\" /></strong>*As of October 7, 2010, for the pixel resolution and color reproduction of calculators. According to a survey by Casio Computer Co., Ltd.<br style=\"word-wrap: break-word;\" />**Using repetition of the following one-hour sequence: Menu display for 5 minutes, Run-Matrix mode calculation for 5 minutes, flashing cursor in Run-Matrix mode for 50 minutes. Based on default settings for display brightness and auto power off. (Refer to the Specifications for exact settings)&nbsp;&nbsp;<strong style=\"word-wrap: break-word;\"><img style=\"word-wrap: break-word; border-style: initial; border-color: initial;\" src=\"http://www.casio.com/resource/images/pressreleases/fx-CG10_Prizm_press_3.jpg\" alt=\"PRIZM fx-CG10\" align=\"right\" /></strong><strong style=\"word-wrap: break-word;\">Picture Plot enables learning from real-life pictures</strong><strong style=\"word-wrap: break-word;\">&nbsp;</strong><br style=\"word-wrap: break-word;\" /><br style=\"word-wrap: break-word;\" />Picture Plot enables users to create their own graphs over pictures displayed on the color LCD. PRIZM&trade; comes with 55 types of color pictures that users can create graphs over, such as a picture of the parabola of jets from a water fountain and an image sequence of a clock&rsquo;s pendulum movement. Users can perform regression calculations from the completed graphs to enhance their understanding of math functions.&nbsp;&nbsp;&nbsp;<strong style=\"word-wrap: break-word;\">Multitude of functions that leverage the color LCD</strong><br style=\"word-wrap: break-word;\" /><br style=\"word-wrap: break-word;\" />Colors can be added to a multitude of graphing objects including dotted lines, circles, and bars, as well as grid lines on graphs, labels of coordinate axes, and coordinate values displayed during tracing. The Color Link function links the colors used in graphs to the designated values in the spreadsheet screen, to aid the visual comprehension of trends and changes in values. PRIZM&trade; automatically color-codes brackets when entering equations with multiple brackets, as a visual aid to facilitate the entry of complex equations.&nbsp;</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('127', 'EN', 'Fore CR5 SRAM Red', '', '', '<p>An all new frame and new graphics package make the Fore CR5 look as  fast as it really is. Now available with SRAM Red componants.</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('128', 'EN', 'Casio FX-115ES', '', '', '<table class=\"defTBL\" style=\"word-wrap: break-word; color: #555555; font-family: Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: left; background-color: #ffffff;\" border=\"0\" width=\"95%\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('129', 'EN', 'Fore CR1', '', '', '<p>Take your riding to the next level with the For&eacute; CR1 full carbon road  bike, built to be extremely durable and responsive. Feel the adrenalin  rush as it accelerates smoothly and corners aggressively. Great for  cross-country riding, a triathlon, and even a race.&nbsp; The new Fezzari  Fore CR1 is the newest bike to the Fezzari carbon road bike family.&nbsp; It  has the same tried and true monocoque carbon frame as the&nbsp; Fore CR2, but  is built with a Shimano 105/Ultegra groupo to help save a little on the  price.</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('130', 'EN', 'Casio DR-210TM', '', '', '<table class=\"defTBL\" style=\"word-wrap: break-word; color: #555555; font-family: Helvetica, sans-serif; font-size: 12px; line-height: 18px; text-align: left; background-color: #ffffff;\" width=\"79%\">INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('131', 'EN', 'Catania', '', '', '<p class=\"MsoPlainText\">The new Catania features carbon fork, carbon seatpost, Ultegra  derailleur, and the new Fezzari Racing Design R3A frame with increased  aerodynamic properties and a sleeker look.</p>INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('132', 'EN', 'Samsung Galaxy Player 5.0', '', '', '<p>Easily connect via Wi-Fi without any monthly service fees. The Samsung Galaxy Player can access the internet and apps anywhere there is an available hotspot. The sleek, lightweight 6.42 oz. design fits in any pocket and the brilliant 5&rdquo; LCD delivers incredible viewing quality.</p>', '', '', '', '', '');
