INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('135', 'EN', 'Creative ZEN Touch 2 MP3 Player 8GB (Black)', '', '', '<p>The ZEN line of portable media players has long been associated with the highest quality in audio playback, and the Creative ZEN Touch 2 continues to uphold this standard. But that\'s not all it offers. What makes this player truly stand out from the rest is that you can stream music wirelessly to compatible Bluetooth&reg; headphones or speakers such as Creative\'s ZiiSound D5 and WP-300, and surf the Internet too.</p>', '', '', '', '', '');
