INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('184', 'EN', 'Samsung 30mm NX Pancake Lens', '', '', '<p>Samsung&rsquo;s compact 30mm pancake lens is the brightest in its class. With its 30mm fixed focal length and maximum aperture of F/2, you can capture exceptionally bright images &mdash; even in low-light situations such as shady forest paths and indoor weddings. You can also create striking, images with shallow depth of field in a variety of settings. And since it&rsquo;s incredibly slim and lightweight, you can take it with you everywhere.</p>', '', '', '', '', '');
