INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('218', 'EN', 'ASUS Essentio CP1130', '', '', '<p>The ASUS new Essentio CP1130 has sleek appearance and small size make it perfectly fit anywhere in your home. At the same time, it provides complete function and delivers ultimate performance for daily computing.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('219', 'EN', 'Series 3 15.6\" Laptop', '', '', '<p>Don\'t be weighed down by wasted time, let your PC come alive when you need it. Your life happens on the go, you don\'t have time to wait for PC to shut down and power up. With Samsung\'s exclusive Fast Start technology, simply close the lid to enter a hybrid sleep mode. When you open the lid, you are up and running in less than 3 sec.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('220', 'EN', 'Series 7 Chronos 14 Notebook', '', '', '<p>Cutting edge design: Sleek aluminum, a stunning screen, cutting edge performance, all seamlessly blended in one of the world&rsquo;s thinnest quad core PC.</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('221', 'EN', 'Series 9 13.3\" Laptop', '', '', '<p>You no longer have to shut down your Samsung computer, just close its lid and it goes into a special powerless sleep mode. When you open the lid your computer comes back to life in 3 seconds and you are exactly at the same place as when you shut the lid. Fast Start saves your data and system configurations to your hard drive and PC memory. So, when you power it back on it is just like you left it.</p>', '', '', '', '', '');
