INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('235', 'EN', 'Everglades Cold Weather Sleeping Bag', '', '', '<p>&bull;  The perfect sleeping bag for extreme sleeping conditions between 10 and 30 degrees<br />&bull;  3 lbs Coletherm&reg; insulation<br />&bull;  33&rdquo; x 85&rdquo;, fits up to 5&rsquo;11&rdquo; tall<br />&bull;  Diamond ripstop cover to polyester liner<br />&bull;  Double-batt, offset quilt construction eliminates potential cold spots<br />&bull;  Essential camping gear, sleeping bag for a better night outdoors<br />&bull;  Semi-sculpted hood surrounds head with warmth, keeps head off the ground<br />&bull;  Fleece foot section provides extra warmth<br />&bull;  Two way zipper for maximum ventilation<br />&bull;  Stuff sack included<br />&bull;  Made in China<br />&bull;  Commercial Machine washable<br />&bull;  Five year limited warranty</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('236', 'EN', 'WeatherMaster Screened 6 Tent With Hinged Door', '', '', '<p>&bull;  6 person tent/ 2 rooms<br />&bull;  17\'x9\' footprint<br />&bull;  Cabin structure; heavier, stronger, better for extended stay camping<br />&bull;  6 ft. 10 in. center height<br />&bull;  2 Doors - Front Door is Hinged (Patent Pending)<br />&bull;  Strong steel and fiberglass frame<br />&bull;  Control airflow with Variflo&trade; adjustable ventilation<br />&bull;  Privacy vent window<br />&bull;  Interior gear pocket<br />&bull;  Electrical access port<br />&bull;  Reverse angle windows<br />&bull;  Easy set up with continuous, color coded pole sleeves and shock-corded poles<br />&bull;  Easy instructions sewn into durable carry bag<br />&bull;  Carry bag also includes separate sacks for poles and stakes<br />&bull;  Fly: Polyester taffeta 75D <br />&bull;  Mesh: Polyester 68D inner tent<br />&bull;  Floor: Polyethylene 1000D-140g/sqm floor<br />&bull;  Limited 1 year warranty<br />&bull;  Made in China</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('237', 'EN', 'Elite Evanston 8 Tent', '', '', '<p>&bull;  8 person/1 room tent<br />&bull;  12\'x12\' footprint<br />&bull;  Exclusive WeatherTec&trade; System<br />Keeps you dry -- Guaranteed&trade;<br />&bull;  Modified dome structure, easy to transport and simple to set up<br />&bull;  6 ft. 2 in. center height<br />&bull;  Hinged door (patent pending)<br />&bull;  Great for family camping, scout leaders, extended camping trips<br />&bull;  Self-rolling windows (patent pending)<br />&bull;  Tent includes remote controlled light<br />&bull;  Light is 100 lumens<br />&bull;  Control airflow with Variflo&trade; adjustable ventilation<br />&bull;  Privacy vent window<br />&bull;  Interior gear pocket<br />&bull;  Electrical access port<br />&bull;  Front Porch and Wings provide great outdoor living space<br />&bull;  Easy set up with continuous, color coded pole sleeves and shock-corded poles<br />&bull;  Easy instructions sewn into durable carry bag<br />&bull;  Carry bag also includes separate sacks for poles and stakes<br />&bull;  Fly: Polyester taffeta 75D <br />&bull;  Mesh: Polyester 68D inner tent<br />&bull;  Floor: Polyethylene 1000D-140g/sqm floor<br />&bull;  Poles: 11 mm, 7.9 mm and 6.3 mm fiberglass<br />&bull;  Limited 1 year warranty<br />&bull;  Made in China</p>', '', '', '', '', '');
INSERT INTO cscart_product_descriptions (`product_id`, `lang_code`, `product`, `shortname`, `short_description`, `full_description`, `meta_keywords`, `meta_description`, `search_words`, `page_title`, `age_warning_message`) VALUES ('238', 'EN', 'Apple iPhone 4S Black', '', '', '<p>16GB iPhone 4S locked AT&amp;T model. Will work on AT&amp;T. Now you can use your voice to use your iPhone. Just talk to Siri as you would to a person: \"Do I need an umbrella?\" or \"Any great burgers around here?\" or \"Where\'s the closest ATM?\" Siri not only understands what you say, it knows what you mean.1 It figures out the right apps to use to find the right answer. Then, just like a personal assistant, it answers you. Siri makes phone calls, sends messages, schedules meetings, sets reminders, and more. How much more? Just ask, and Siri tells you that, too. Two cores in the A5 chip deliver up to two times more power and up to seven times faster graphics.2 And you\'ll feel the effects. Fast. iPhone 4S is quick and responsive, which makes all the difference when you\'re launching apps, browsing the web, gaming, and doing just about everything. And no matter what you\'re doing, you can keep on doing it. Because the A5 chip is so power-efficient, iPhone 4S has outstanding battery life. This just might be the best camera ever on a phone. And with all-new optics, it just might be the only camera you need. Because behind every shot, you\'ve got 8-megapixel resolution and a custom lens with a larger f/2.4 aperture. Not to mention an improved backside illumination sensor, excellent auto white balance, advanced color accuracy, face detection, and reduced motion blur. It all means that no matter how many people, how much light, and how much action you capture, everything looks exactly as it should. And just wait until you see the photos. Shoot stunning 1080p HD video everywhere you go. With all-new optics, the light is always right, the color is always vivid, and everything will look even better than you remember. Video stabilization steadies shaky shots. And you can edit video right on iPhone and share your lush life as fast as you shoot.</p>', '', '', '', '', '');
