REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('DA', 'text_exim_import_features_note', '<b>Product features import format</b> <code> {$ldelim}%Feature ID%{$rdelim} (%Group name%) %Feature name%: %Feature type%[%Feature value%] </code> hvor, <p> %Feature ID% - the id of feature,<br /> %Group name% - navnet på funktion gruppen,<br /> %Feature name% - navnet på funktionen,<br /> %Feature type% - denne feature type (C - checkbox, M - flere afkrydsningsfelter, S - tekst markere boks, N - nummer ved at vælge boks, E - udvidet valgboks, T - Nem tekst, O - nummer, D - dato),<br /> %Feature value% - det karakteristiske værdi (flere værdier kan afgrænses med komma) </ p> Flere funktioner skal afgrænses af semikolon. </ p> <b> Eksempel: </ b> <br /> <code> ISBN: T [1233423423]; Udgivelsesdato: D [05/05/07]; Farve: S [Rød, Grøn, Blå] </code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('DA', 'text_exim_import_files_note', '<b>Fil import format</b><code>%File location%</code>hvor,<p>%File location% - kan være en absolut eller relativ sti på en server filsystem eller URL <br /> Flere filer skal afgrænses med komma. </ p> <b> Eksempel 1: </ b> <br /> <code> file1.jpg </code> <b> Eksempel 2: </ b> <br /> <code> backup/file1.zip, images/file2.jpg </code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('DA', 'text_exim_import_images_note', '<b>Image import format</b><code>%Image location%#%Alternative text%</code>where,<p>%Image location% - kan være en absolut eller relativ sti på en server filsystem eller URL, <br />% Alternativ tekst% - billede alternativ tekst </ p> <b> Eksempel 1: </ b> <br /> <code> images / product_images / apples.jpg # Æbler </ code> <b> Eksempel 2: </ b> <br /> <code> http://www.site.com/apples.jpg # Æbler </ code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('DA', 'text_exim_import_notice', 'Nedenfor er en liste over de felter, som dine data fil kan indeholde. Den <b> fremhævede </ b> felter er obligatoriske. Hvis du importerer data med særlige symboler (komma, semikolon osv) skal du sørge for at have citeret felter.');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('DA', 'text_exim_import_options_note', '<b> Produkt valgmuligheder import-format </ b> \n\n\n\n\n\n\n<code>\n\n\n\n{$ldelim}%Option ID[_L]%{$rdelim}%Option name%: %Option type%[{$ldelim}%Variant 1 ID%{$rdelim}%Variant 1%,{$ldelim}%Variant 2 ID%{$rdelim}%Variant 2%,{$ldelim}%Variant N ID%{$rdelim}%Variant N%]\n\n\n\n</code>\n\n\n\n\n\n\n\nhvor,\n\n\n\n<p>\n\n\n\n% Option ID% - id for den mulighed, <br />\n\n\n\n<b> L </ b> - valgfri parameter, betyder, at det er en sammenkædet global løsning af produktet. <br />\n\n\n\n% Option navn% - navnet på den mulighed, <br />\n\n\n\n% Option type% - mulighed type, kan være følgende: <br />\n\n\n\n<b> S </ b> - selectbox, <br />\n\n\n\n<b> R </ b> - radiogroup, <br />\n\n\n\n<b> C </ b> - afkrydsningsfeltet, <br />\n\n\n\n<b> I </ b> - enkel indgang, <br />\n\n\n\n<b> T </ b> - textarea. <br /> \n\n\n</p>\n\n\n\n<p>\n\n\n\n% Variant N ID% - id for den variant, <br />\n\n\n\n% Variant N% - navnet på den variant. Varianter skal angives, hvis indstillingen type er en selectbox eller radiogroup alene.\n\n\n\n</ p>\n\n\n\n\n\n\n\n<p>\n\n\n\nFlere muligheder skal afgrænses af semikolon.\n\n\n\n</ p>\n\n\n\n\n\n\n\n<b> Eksempler: </ b> <br />\n\n\n\n<p>\n\n\n\nSimpel tekst valgmuligheder:\n\n\n\n<code>\n\n\n\nDin alder: I; Fødselsdato: I; Noter: T\n\n\n\n\n</code>\n\n\n\n</p>\n\n\n\n<p> \n\n\nValg med varianter:\n\n\n\n<code>\n\n\n\nFarve: S [Rød, Grøn, Blå], Size: R [X, XL, XXL]\n\n\n\n</ code>\n\n\n\n</ p>');
