REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('ES', 'text_exim_import_files_note', '<b>File import format</b><code>%File location%</code>where,<p>%File location% - can be absolute or relative path on server filesystem or URL<br />Several files must be delimited by comma.</p><b>Example 1:</b><br /><code>file1.jpg</code><b>Example 2:</b><br /><code>backup/file1.zip, images/file2.jpg</code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('ES', 'text_exim_import_images_note', '<b>Image import format</b><code>%Image location%#%Alternative text%</code>where,<p>%Image location% - can be absolute or relative path on server filesystem or URL,<br />%Alternative text% - Image alternative text</p><b>Example 1:</b><br /><code>images/product_images/apples.jpg#Apples</code><b>Example 2:</b><br /><code>http://www.site.com/apples.jpg#Apples</code>');
