REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('FR', 'text_exim_import_features_note', '<b>Product features import format</b><code>{$ldelim}%Feature ID%{$rdelim} (%Group name%) %Feature name%: %Feature type%[%Feature value%]</code>where,<p>%Feature ID% - the id of feature,<br />%Group name% - the name of feature group,<br />%Feature name% - the name of feature,<br />%Feature type% - the feature type (C - checkbox, M - multiple checkboxes, S - text select box, N - number select box, E - extended selectbox, T - simple text, O - number, D - date),<br />%Feature value% - the feature value (several values can be delimited by comma)</p><p>Several features must be delimited by semicolon.</p><b>Example:</b><br /><code>ISBN: T[1233423423]; Release date: D[05/05/07]; Color: S[Red]</code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('FR', 'text_exim_import_files_note', '<b>File import format</b><code>%File location%</code>where,<p>%File location% - can be absolute or relative path on server filesystem or URL<br />Several files must be delimited by comma.</p><b>Example 1:</b><br /><code>file1.jpg</code><b>Example 2:</b><br /><code>backup/file1.zip, images/file2.jpg</code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('FR', 'text_exim_import_images_note', '<b>Image import format</b><code>%Image location%#%Alternative text%</code>where,<p>%Image location% - can be absolute or relative path on server filesystem or URL,<br />%Alternative text% - Image alternative text</p><b>Example 1:</b><br /><code>images/product_images/apples.jpg#Apples</code><b>Example 2:</b><br /><code>http://www.site.com/apples.jpg#Apples</code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('FR', 'text_exim_import_notice', 'Voici la liste des champs que votre fichiers de données peut contenir. Les champs <b>surlignés</b> sont requis.');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('FR', 'text_exim_import_options_note', '<b>Product options import format</b><code>{$ldelim}%Option ID[_L]%{$rdelim}%Option name%: %Option type%[{$ldelim}%Variant 1 ID%{$rdelim}%Variant 1%,{$ldelim}%Variant 2 ID%{$rdelim}%Variant 2%,{$ldelim}%Variant N ID%{$rdelim}%Variant N%]</code>where,<p>%Option ID% - the id of the option,<br />&nbsp;&nbsp;<b>L</b> - optional parameter, means that it is a linked global option of the product.<br />%Option name% - the name of the option,<br />%Option type% - the option type, can be the following:<br />&nbsp;&nbsp;<b>S</b> - selectbox,<br />&nbsp;&nbsp;<b>R</b> - radiogroup,<br />&nbsp;&nbsp;<b>C</b> - checkbox,<br />&nbsp;&nbsp;<b>I</b> - simple input,<br />&nbsp;&nbsp;<b>T</b> - textarea.<br /></p><p>%Variant N ID% - the id of the variant,<br />%Variant N% - the name of the variant. Variants must be specified if the option type is a selectbox or radiogroup only.</p><p>Several options must be delimited by semicolon.</p><b>Examples:</b><br /><p>Simple text options:<code>Your age: I; Date of birth: I; Notes: T</code></p><p>Options with variants:<code>Color: S[Red, Green, Blue]; Size: R[X, XL, XXL]</code></p>');
