REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('RO', 'text_exim_import_features_note', '<b>Formatul importării de caracteristici produs</b> <code> {$ldelim}%Feature ID%{$rdelim} (%Group name%) %Feature name%: %Feature type%[%Feature value%] </code> unde, <p> %Feature ID% - id-ul caracteristicii,<br /> %Group name% - numele grupului de caracteristici,<br /> %Feature name% - numele caracteristicii,<br /> %Feature type% - tipul caracteristicii (C - căsuță de bifare, M - căsuțe de bifare multiple, S - casetă text, N - numărul căsuței de selectare, E - căsuță de selectare extinsă, T - text simplu, O - număr, D - dată),<br /> %Feature value% - valoarea caracteristicii (mai multe valori pot fi delimitate prin virgulă) </p> <p> Mai multe caracteristici trebuie delimitate prin punct și virgulă. </p> <b>Exemplu:</b><br /> <code> ISBN: T[1233423423]; Data lansării: D[05/05/07]; Culoare: S[Roșu, Verde, Albastru] </code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('RO', 'text_exim_import_files_note', '<b>Formatul importatorului de fișiere</b><code>%Locația fișierului%</code>unde,<p>%Locația fișierului% - poate fi absolută sau relativă pe server sau URL<br />Mai multe fișiere trebuie delimitate cu virgulă.</p><b>Exemplu 1:</b><br /><code>file1.jpg</code><b>Exemplu 2:</b><br /><code>backup/file1.zip, images/file2.jpg</code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('RO', 'text_exim_import_images_note', '<b>Formatul imporatorului de imagini</b><code>%Locația imaginii%#%Textul alternativ%</code>unde,<p>%Locația imaginii% - poate fi absolută sau relativă pe server sau URL,<br />%Textul alternativ% - textul alternativ al imaginii</p><b>Exemplu 1:</b><br /><code>images/product_images/apples.jpg#Apples</code><b>Exemplu 2:</b><br /><code>http://www.site.com/apples.jpg#Apples</code>');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('RO', 'text_exim_import_notice', 'Mai jos se află o listă de câmpuri pe care le poate conține fișierul dvs. <b>subliniate</b> sunt obligatorii. Dacă importați date cu simboluri speciale (virgule, punct și virgulă etc.) vă rugăm să vă asigurați că sunt puste în ghirimele.');
REPLACE INTO cscart_language_values (`lang_code`, `name`, `value`) VALUES ('RO', 'text_exim_import_options_note', '<b>Formatul importatorului de opțiuni al produselor</b><code>{$ldelim}%Option ID[_L]%{$rdelim}%Option name%: %Option type%[{$ldelim}%Variant 1 ID%{$rdelim}%Variant 1%,{$ldelim}%Variant 2 ID%{$rdelim}%Variant 2%,{$ldelim}%Variant N ID%{$rdelim}%Variant N%]</code>unde,<p>%Option ID% - este id-ul opțiunii,<br />&nbsp;&nbsp;<b>L</b> - parametru opțional, înseamnă că va fi asimilat opțiunilor globate a produsului.<br />%Option name% - numele opțiunii,<br />%Option type% - tipul opțiunii, poate fi următorul:<br />&nbsp;&nbsp;<b>S</b> - căsuță de selectare,<br />&nbsp;&nbsp;<b>R</b> - radiogrup,<br />&nbsp;&nbsp;<b>C</b> - căsuță de bifare,<br />&nbsp;&nbsp;<b>I</b> - introducere simplă,<br />&nbsp;&nbsp;<b>T</b> - zonă de text.<br /></p><p>%Variant N ID% - id-ul variantei,<br />%Variant N% - numele variantei. Variantele trebuie specificate doar dacă tipul opțiunilor e o căsuță de selectare sau un radiogrup.</p><p>Mai multe opțiuni trebuie delimitate prin punct și virgulă.</p><b>Exemple:</b><br /><p>Opțiuni de text simplu:<code>Vârsta dvs. : I; Data nașterii: I; Note: T</code></p><p>Opțiuni variante:<code>Culoare: S[Roșu, Verde, Albastru], Mărime: R[X, XL, XXL]</code></p>');
